/**
 * Language pack for Mandarin Chinese written with simplified characters, with
 * focus on Mandarin as commonly spoken in PRC.
 */
export default class SimplifiedChineseLanguagePack extends DefaultLanguagePack {
    constructor(locale: any);
    postprocessSynthetic(sentence: any, program: any): any;
    detokenize(buffer: any, prevtoken: any, token: any): any;
    pluralize(noun: any): string | undefined;
    toVerbPast(phrase: any): string;
    isGoodWord(word: any): boolean;
    isGoodSentence(sentence: any): boolean;
    isGoodNumber(number: any): boolean;
    isGoodPersonName(word: any): boolean;
    ABBREVIATIONS: {};
}
import DefaultLanguagePack from "./default";
