/// <reference types="node" />
/**
 * A Buffer-like class that automatically grows in size.
 */
export default class GrowableBuffer {
    private _size;
    private _capacity;
    private _buffers;
    private _offsets;
    constructor();
    get length(): number;
    toBuffer(): Buffer;
    private _ensureSize;
    private _findBuffer;
    writeUInt8(value: number, offset?: number): void;
    writeUInt16LE(value: number, offset?: number): void;
    writeUInt32LE(value: number, offset?: number): void;
    writeBuffer(fromBuffer: Buffer, offset?: number): void;
}
