/**
 * Progbar class copied from keras (https://github.com/fchollet/keras/)
 * and then translated from Python to Javascript
 *
 * Displays a progress bar.
 */
export default class ProgressBar {
    private width;
    private target;
    private sum_values;
    private unique_values;
    private start;
    private total_width;
    private seen_so_far;
    private verbose;
    private _isatty;
    private _previous_pct;
    constructor(target: number, { width, verbose }?: {
        width?: number | undefined;
        verbose?: number | undefined;
    });
    update(current: number, values?: Array<[string, number]>, exact?: Array<[string, number]>): void;
    add(n: number, values?: Array<[string, number]>): void;
}
