let numberGiver = require('./numberGiver.js')
let responseGiver = require('./responseGiver.js')

async function wordsToNumber(amount){
	let model = {}
	try{
		model.status = 'success'
		model.amount = amount
		model = await numberGiver.replacer(model)
		model = await numberGiver.trimmer(model)
		model = await numberGiver.billionConverter(model)
		model = await numberGiver.crConverter(model)
		model = await numberGiver.millionsConverter(model)
		model = await numberGiver.lacksConverter(model)
		model = await numberGiver.thousandConverter(model)
		model = await numberGiver.hundredConverter(model)
		model = await numberGiver.tensUnitConverter(model)
		model = await numberGiver.adder(model)
		model = await numberGiver.quomaAdder(model)
		model = await numberGiver.tagsDeleter(model)
		return (model)
	}
	catch(e){
		model.status = 'failure'
		model.response = responseGiver[model.stage]
		model.NOTE = "Please email us on rohansolse@gmail.com for NPM related suggestions/bugs(with input)."
		model = await numberGiver.tagsDeleter(model)
		return (model)
	}
}
module.exports = wordsToNumber